import { useNavigate } from "react-router-dom";
import { useState } from 'react';

function UserLogin(){
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [done, setDone] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSignUp = async (e) => {
    e.preventDefault();
    setDone("Submitting...");


    try {
      console.log("Sending data:", { username, password });
      const response = await axios.post("http://localhost:5000/Login", {
        username,
        password,
      });

      setMessage(response.data.message);
      setDone("Registration successful");
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "An error occurred");
      setDone("Failed to register");
    }
  };


    return(
        <>
        <div className="Login">
            <div className="login-box">
                <form className='login-form'>
                <input type="text" placeholder="Enter Username" className="username" required></input>
                <input type="password" placeholder="Enter Password" className="username"></input>
                <button className="login-button" type="submit">
                    Login
                </button>
                </form>
                <p>Don't have account? <spam className="createAcc" onClick={() => navigate("/signup")}>Create Account</spam></p>
            </div>

        </div>

        </>
    )
}
export default UserLogin;
