import { useState } from "react";
import "./UserSignIn.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function UserSignIn() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  const [done, setDone] = useState("");

  const navigate = useNavigate();

  const handleSignUp = async (e) => {
    e.preventDefault();
    setDone("Submitting...");

    if (password !== confirmPassword) {
      setMessage("Passwords do not match!");
      return;
    }

    try {
      console.log("Sending data:", { username, password });
      const response = await axios.post("http://localhost:5000/sign-up", {
        username,
        password,
      });

      setMessage(response.data.message);
      setDone("Registration successful");
      if(response.data.message === "User registered successfully") {
        navigate("/TaskList");
      }
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "An error occurred");
      setDone("Failed to register");
    }
  };

  return (
    <div className="Sign-in">
      <div className="sign-in-box">
        <form className="sign-in-form" onSubmit={handleSignUp}>
          {message && <p className="sign-err">{message}</p>}
          <input
            type="text"
            placeholder="Enter your username"
            className="sign-username"
            required
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter your password"
            className="sign-username"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
          <input
            type="password"
            placeholder="Confirm your password"
            className="sign-username"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <button type="submit" className="sign-in-button">
            Sign Up
          </button>
        </form>
        <p>
          Already have an account?{" "}
          <span className="spamLogin" onClick={() => navigate("/")}>
            Login
          </span>
        </p>
      </div>
    </div>
  );
}

export default UserSignIn;
