import './UserLogin.css';
import { useNavigate } from "react-router-dom";
import { useState } from 'react';
import axios from 'axios';

function UserLogin({setUserId}){
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [done, setDone] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setDone("Submitting...");

    try {
      console.log("Sending data:", { username, password });
      const response = await axios.post("http://localhost:5000/Login", {
        username,
        password,
      });

      setMessage(response.data.message);
      setDone("Registration successful");

      // Store the userId after successful login
      localStorage.setItem('userId', response.data.userId);  // Store userId in localStorage
      setUserId(response.data.userId)

      // Redirect to TaskList
      navigate("/TaskList");

    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "An error occurred");
      setDone("Failed to register");
    }
  };

  if(message==="Login successful"){
    navigate("/TaskList")
  }


    return(
        <>
        <div className="Login">
            <div className="login-box">
                <form className='login-form' onSubmit={handleLogin}>
                <input type="text" placeholder="Enter Username" className="username" required onChange={(e)=> {setUsername(e.target.value)}}></input>
                <input type="password" placeholder="Enter Password" className="username" onChange={(e)=> {setPassword(e.target.value)}}></input>
                <button className="login-button" type="submit">
                    Login
                </button>
                </form>
                <p>Don't have account? <spam className="createAcc" onClick={() => navigate("/signup")}>Create Account</spam></p>
                {message && <p>{message}</p>}
            </div>
        </div>
        </>
    )
}
export default UserLogin;
