import { useState } from 'react';
import './UserSignIn.css';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
function UserSignIn(){
    const [username, setUsername]=useState("");
    const [password, setPassword]=useState("");
    const [done,setDone]=useState("");
    const [message, setMessage] = useState("");


    const navigate = useNavigate();
    const handleSignUp=async (e)=>{
        e.preventDefault();
        setDone("")

    try {
      const response = await axios.post("http://localhost:5000/register", {
        username,
        password,
      });

      setMessage(response.data.message);
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "An error occurred");
    }
    };
    return (
        <>
        <div className="Sign-in">


            <div className="sign-in-box">
                <form className="sign-in-form" onSubmit={handleSignUp}>
                <input type="text" placeholder="Enter your username" className="sign-username" required onChange={(e)=> {setUsername(e.target.value)}}></input>
                <h1>your name : {username}</h1>
                <input type="password" placeholder="Enter your password" className="sign-username" required onChange={(e)=> {setPassword(e.target.value)}}></input>
                <h2>your password :{password}</h2>
                <input type="password" placeholder="confirm password" className="sign-username" required></input>
                <h1> status done : {done}</h1>
                <button type="submit" className="sign-in-button">Sign Up</button>
                </form>
                <p>Already have account? <spam className="spamLogin" onClick={() => navigate("/")}>Login</spam></p>
            </div>
        </div>
        </>
    )
}

export default UserSignIn;
